INSERT INTO `t_course` VALUES ('3071', '20,', '2', '0', '人教小学语文同步课堂一年级下', '2', '1', '1', null, '马洪英', '1', '10.00', null, null, '307101,307102,307103,307104,307105,307106,307107,307108,', '3', '1/3071.png', null, '本册以专题组织单元，以整合的方式组织教材内容。共设有：多彩的春天，家庭生活，保护环境等8个专题。每个单元从导语到识字课，课文，语文园地都围绕本单元专题安排。 ', '0', '1', '2015-01-17 18:43:20');
INSERT INTO `t_course` VALUES ('3072', '20,', '5', '0', '人教小学语文同步课堂二年级下', '2', '1', '1', null, '马洪英', '1', '10.00', null, null, '307201,307202,307203,307204,307205,307206,307207,307208,', '3', '1/3072.png', null, '认识400个字，会写300个字。要求认识的字能读准字音，结合词句了解意思，不作字形分析，不要求书写。要求会写的字能读准字音，认清字形，理解意思，正确书写，练习在口头和书面表达中运用。有主动识字的愿望，在生活中自主识字。养成学生良好的写字习惯', '0', '1', '2015-01-17 18:43:50');
INSERT INTO `t_course` VALUES ('3073', '20,', '10', '0', '人教小学语文同步课堂三年级下', '2', '1', '1', null, '孙芬', '1', '10.00', null, null, '307301,307302,307303,307304,307305,307306,307307,307308,', '3', '1/3073.png', null, '本册教材共有8个单元,32篇课文，每个单元包括导语、课文、语文百花园三部分。要求学生认识250个生字，学习观察大自然，观察社会，书面与口头表达结合表达自己的观察所得。', '0', '1', '2015-01-17 18:44:20');
INSERT INTO `t_course` VALUES ('3074', '20,', '11', '0', '人教小学语文同步课堂四年级下', '2', '1', '1', null, '丁纳', '1', '10.00', null, null, '307401,307402,307403,307404,307405,307406,307407,307408,', '3', '1/3074.png', null, '本册共有课文32篇，要求学生认字200个，会写200个；能初步把握文章的主要内容，思想感情，能复述叙事课文的大意；会写简短的书信便条，能修改习作中有明显错误的词句。', '0', '1', '2015-01-17 18:44:32');
INSERT INTO `t_course` VALUES ('3075', '20,', '12', '0', '人教小学语文同步课堂五年级下', '2', '1', '1', null, '丁纳', '1', '10.00', null, null, '307501,307502,307503,307504,307505,307506,307507,307508,', '3', '1/3075.png', null, '全册共有课文28篇，精读和略读各14篇。这七组教材，每组由“导语”“课例”“口语交际•习作”“回顾•拓展”四部分组成。本学期将完成小学阶段认识3000个常用字的识字任务。', '0', '1', '2015-01-17 18:44:57');
INSERT INTO `t_course` VALUES ('3076', '20,', '13', '0', '人教小学语文同步课堂六年级下', '2', '1', '1', null, '丁纳', '1', '10.00', null, null, '307601,307602,307603,307604,307605,', '3', '1/3076.png', null, '本册教材共有课文21篇。要求学生会写80个字，累计2500个字；阅读方面能借助工具书……', '0', '1', '2015-01-17 18:45:09');
INSERT INTO `t_course` VALUES ('3077', '20,', '2', '0', '人教小学数学一年级下－2012', '2', '2', '1', null, null, '1', '10.00', null, null, '307701,307702,307703,307704,307705,307706,307707,', '3', '1/3077.png', null, '本学期的教学目标是学生能够认识计数单位“一”和“十”；能够比较熟练地计算20以内的退位减法；能用自己的语言描述长方形、正方形边的特征；认识人民币单位元、角、分等。', '0', '1', '2015-01-17 18:45:29');
INSERT INTO `t_course` VALUES ('3079', '20,', '5', '0', '人教小学数学二年级下－2013', '2', '2', '1', null, null, '1', '10.00', null, null, '307901,307902,307903,307904,307905,307906,307907,307908,307909,', '3', '1/3079.png', null, '本学期数学教学目标：认识计数单位百和千；会口算百以内的两位数加、减两位数；知道除法的含义；认识角，认识质量单位克和千克；理解统计的意义，会用简单的方法收集和整理数据等。', '0', '1', '2015-01-17 18:46:33');
INSERT INTO `t_course` VALUES ('3081', '20,', '10', '0', '人教小学数学三年级下', '2', '2', '1', null, null, '1', '10.00', null, null, '308101,308102,308103,308104,308105,308106,308107,308108,308109,', '3', '1/3081.png', null, '本册主要掌握的知识点是两位数乘除法，小数；认识位置与方向；理解面积，面积单位；认识时间单位年、月、日及24时计时法；能够用所学的乘、除法知识解决生活中的简单问题。', '0', '1', '2015-01-17 18:46:46');
INSERT INTO `t_course` VALUES ('3082', '20,', '11', '0', '人教小学数学四年级下', '2', '2', '1', null, null, '1', '10.00', null, null, '308201,308202,308203,308204,308205,308206,308207,308208,', '3', '1/3082.png', null, '本册教学主要包括：小数的意义与性质（重难点），小数的加法和减法，四则运算，运算定律与简便计算，三角形，位置与方向，折线统计图，数学广角和数学综合运用活动等。', '0', '1', '2015-01-17 18:47:08');
INSERT INTO `t_course` VALUES ('3083', '20,', '12', '0', '人教小学数学五年级下', '2', '2', '1', null, null, '1', '10.00', null, null, '308301,308302,308303,308304,308305,308306,308307,', '3', '1/3083.png', null, '本册的重难点在数与代数部分因数与倍数；分数的意义和性质以及分数的加法、减法；空间与图形”领域教学图形的变换和长方体和正方体；统计与概率部分众数和复式折线统计图。', '0', '1', '2015-01-17 18:47:21');
INSERT INTO `t_course` VALUES ('3084', '20,', '13', '0', '人教小学数学六年级下', '2', '2', '1', null, null, '1', '10.00', null, null, '308401,308402,308403,308404,308405,', '3', '1/3084.png', null, '本册教学包括下面一些内容：负数、圆柱与圆锥、比例、统计、数学广角、整理和复习等。圆柱与圆锥、比例和整理和复习是本册教材的重点教学内容，也是视频教学的重点部分。', '0', '1', '2015-01-17 18:47:34');
INSERT INTO `t_course` VALUES ('3085', '20,', '10', '0', '人教PEP小学英语三年级下－2012', '2', '3', '1', null, '万玉英', '1', '10.00', null, null, '308501,308502,308503,308504,308505,308506,', '3', '1/3085.png', null, '本学期需要达成的目标：能听懂、会说12组会话，并能进行简单的交流；能听说、认读（包括动物、食品、数字、同学、家庭成员、数字等6个话题）的单词，并能简单的运用。', '0', '1', '2015-01-17 18:47:51');
INSERT INTO `t_course` VALUES ('3086', '20,', '11', '0', '人教PEP小学英语四年级下－2013', '2', '3', '1', null, '李艳', '1', '10.00', null, null, '308601,308602,308603,308604,308605,308606,', '3', '1/3086.png', null, '人教PEP小学英语四年级下－2013', '0', '1', '2015-01-17 18:42:54');
INSERT INTO `t_course` VALUES ('3087', '20,', '11', '0', '人教PEP小学英语四年级下－2003', '2', '3', '1', null, 'candy', '1', '10.00', null, null, '308701,308702,308703,308704,308705,308706,', '3', '1/3087.png', null, '本册教材分为六个单元，要求学生能听懂、会说12组会话，并能进行简单的交流；能听、说 认读60个单词和词组并能听、说、读、写61个单词和24个句子（包括学校、时间等八个话题）。', '0', '1', '2015-01-17 18:48:14');
INSERT INTO `t_course` VALUES ('3088', '20,', '12', '0', '人教PEP小学英语五年级下', '2', '3', '1', null, '李艳', '1', '10.00', null, null, '308801,308802,308803,308804,308805,308806,', '3', '1/3088.png', null, '本学期教学目标是要求学生养成良好的听、读、说英语的习惯；初步养成良好的书写习惯；能按三会与四会的要求掌握所学词语；运用所学进行简单的日常交流并做到大胆开口，发音清楚正确。\n', '0', '1', '2015-01-17 18:53:58');
INSERT INTO `t_course` VALUES ('3089', '20,', '13', '0', '人教PEP小学英语六年级下', '2', '3', '1', null, '李艳，jean', '1', '10.00', null, null, '308901,308902,308903,308904,', '3', '1/3089.png', null, '本学期教学目标是要求学生养成良好的听、读、说英语的习惯；初步养成良好的书写习惯；能按三会与四会的要求掌握所学词语；运用所学进行简单的日常交流并做到大胆开口，发音清楚正确。\n', '0', '1', '2015-01-17 18:54:21');
INSERT INTO `t_course` VALUES ('4005', '27,', '15,16', '0', '中考语文满分作文必备技巧', '2', '1', '2', null, null, '1', '10.00', null, null, '400501,400502,400503,400504,', '2', '1/4005.png，0/4005.png', null, '众所周知，语文成绩的提高与作文关系密切。如何提高学生的作文水平，是提高语文成绩的第一要务。这里我们汇总了中考常见作文类型、应考指导及一些实用的写作技巧，希望对初三生备考会有所帮助。', '0', '1', '2015-01-19 15:49:48');
INSERT INTO `t_course` VALUES ('4016', '27,', '14,16', '0', '中考数学方程专题复习突破', '2', '2', '2', null, null, '1', '10.00', null, null, '401601,401602,401603,401604,401605,', '3', '1/4016.png', null, '方程的概念和原理了解， 一元一次、一元二次方程，二元一次方程组的解法；方程的有关应用题(特别是行程、工程问题)', '0', '1', '2015-01-19 15:49:59');
INSERT INTO `t_course` VALUES ('6001', '18,19,', '8,9,,,', '0', '贝瓦早教动画片', '0', '0', '2', null, null, '1', '10.00', null, null, '600101,', '3', '1/6001.png', null, '贝瓦是一只快乐的小河狸，是小朋友们最亲密的小伙伴。他生性开朗活泼，喜好探索未知事物。勤劳勇敢，富有极强的正义感，喜欢帮助他人。富有想象力，试图实现脑袋里产生的各种奇妙的想法。对待困难不会退缩，勇往直前。小朋友们快来看看贝瓦身上发生了什么有趣', '0', '1', '2015-01-19 09:32:20');
INSERT INTO `t_course` VALUES ('6002', '18,19,', '8,9,,,', '0', '贝瓦小课堂', '0', '0', '2', null, null, '1', '10.00', null, null, '600201,', '3', '1/6002.png', null, '贝瓦小课堂摆脱了枯燥乏味的灌输教育法,通过动画的形式,教授小朋友知识,提高孩子的认知能力,让宝宝在小课堂中快乐学习。', '0', '1', '2015-01-19 09:32:32');
INSERT INTO `t_course` VALUES ('6003', '19,', ',,', '0', '米奇妙妙屋第一季', '0', '0', '2', null, null, '1', '10.00', null, null, '600301,', '3', '1/6003.png', null, '米奇和米妮，唐老鸭，黛丝，高飞，与布鲁托等最受欢迎的迪士尼明星，为全球学龄前幼童带来的3D动画卡通。每一集节目中出现的问题都是小朋友生活中经常遭遇的情境，米奇和好朋友们将以生动的对白与表情，邀请小朋友协助他们一起完成任务。这也是一部强调幼儿', '0', '1', '2015-01-19 09:32:42');
INSERT INTO `t_course` VALUES ('6005', '18,', '8', '0', '布奇乐乐园1-2岁', '0', '0', '2', null, null, '1', '10.00', null, null, '600501,', '3', '1/6005.png', null, '布奇、阿迪、小米、莫莫、小艾是布奇乐乐园的五个小伙伴。布奇是小狮子，他很勇敢；阿迪是小猴子，他爱思考；小米是小松鼠，她很细心；莫莫是小熊猫，他很大方；小艾是个小女孩，她热情、快乐，喜欢帮助别人。五个小伙伴一起生活，一起学习，他们乐于探索，认', '0', '1', '2015-01-19 09:33:10');
INSERT INTO `t_course` VALUES ('6006', '18,', '9', '0', '布奇乐乐园2-3岁', '0', '0', '2', null, null, '1', '10.00', null, null, '600601,', '3', '1/6006.png', null, '布奇、阿迪、小米、莫莫、小艾是布奇乐乐园的五个小伙伴。布奇是小狮子，他很勇敢；阿迪是小猴子，他爱思考；小米是小松鼠，她很细心；莫莫是小熊猫，他很大方；小艾是个小女孩，她热情、快乐，喜欢帮助别人。五个小伙伴一起生活，一起学习，他们乐于探索，认', '0', '1', '2015-01-19 09:33:22');
INSERT INTO `t_course` VALUES ('6007', '18,', '9,,,', '0', '亲宝儿歌专区', '0', '0', '2', null, null, '1', '10.00', null, null, '600701,600702,', '3', '1/6007.png', null, '亲宝儿歌内容多反映儿童的生活情趣，传播生活，生产知识等.词句音韵流畅，易于上口，曲调接近，语言音调，节奏轻快，有独唱或对唱。儿歌动画,画面色彩鲜明，情节引人入胜，深受小朋友们的喜爱。', '0', '1', '2015-01-19 09:33:38');
INSERT INTO `t_course` VALUES ('6008', '18,', '8', '0', '米卡宝宝版', '0', '0', '2', null, null, '1', '10.00', null, null, '600801,', '3', '1/6008.png', null, '1~2岁，是宝宝各大能力发展的黄金阶段。爸爸妈妈们也开始想进行更系统的早教，但究竟自己该做什么，要让宝宝学什么呢？米卡宝宝版已为您排好系统性的全方位学习主题！', '0', '1', '2015-01-19 09:39:05');
INSERT INTO `t_course` VALUES ('6009', '18,19,', '9,,,', '0', '亲宝诗词', '0', '0', '2', null, null, '1', '10.00', null, null, '600901,', '3', '1/6009.png', null, '亲宝诗词是精选深受广大群众所喜爱的诗词；每首都是精心打造，全新的视觉体验，不一样的童话场景让您有不一样的视觉感受。', '0', '1', '2015-01-19 09:39:21');
INSERT INTO `t_course` VALUES ('6010', '19,', '4,6,3', '0', '积木宝贝闯世界', '0', '0', '2', null, null, '1', '10.00', null, null, '601001,', '3,4', '1/6010.png，0/6010-3.png', null, '《闯世界》栏目，希望通过益智类游戏传递先进科学的早教知识，通过行业类权威专家点评指导传播教养方式。让中国各地的父母能通过电视，读懂孩子、分享孩子成长好时光！', '3', '1', '2015-01-19 17:02:26');
INSERT INTO `t_course` VALUES ('6011', '19,', '4,6,3', '0', '亲子沟通秘籍——静观育儿', '0', '0', '2', null, null, '1', '10.00', null, null, '601101,', '3,4', '1/6011.png，0/6011-3.png', null, '《静观育儿》，国内第一档科学育儿脱口秀，关注妈妈们最困惑的育儿难题，传递系统的科学育儿知识，“静”距离为您打造私家育儿方案。', '0', '1', '2015-01-19 17:06:34');
INSERT INTO `t_course` VALUES ('6012', '19,', '', '0', '佟老师：全面备战幼升小', '0', '0', '2', null, null, '1', '10.00', null, null, '601201,', '2,4', '1/6012.png,0/6012.png，0/6012-3.png', null, '从幼儿园生活过渡到小学生活是儿童成长过程中的一个重大的转折点。充分的入学准备，能为孩子入学后的发展奠定良好的基础。那么，做为家长，该为孩子做些什么呢？', '1', '1', '2015-01-19 16:51:45');
INSERT INTO `t_course` VALUES ('6016', '19,', ',,', '0', '奥尔夫幼儿园', '0', '0', '2', null, null, '1', '10.00', null, null, '601601,', '3', '1/6016.png', null, '幼儿音乐教育是幼儿教育环节当中一个重要的组成部分。儿童的天性是活波好动的。孩子们通常会被奇妙的声响所吸引，也就会很自然的接受和进入音乐，如果有一种音乐活动能够巧妙的融入到孩子生活的个个环节以及其他的教育活动中。音乐将会自然的流淌进孩子的心灵', '0', '1', '2015-01-19 09:46:51');
INSERT INTO `t_course` VALUES ('6017', '18,19,', '9,,,', '0', '亲宝咕力咕力', '0', '0', '2', null, null, '1', '10.00', null, null, '601701,', '3', '1/6017.png', null, '在很久很久之前，大海中间有一座小岛。岛上茂密的森林里居住着相亲相爱的咕力咕力家族。它们快乐、阳光、爱学习、热情开朗、充满爱心……在这个神奇的小岛上，每天都在上演着有趣的小故事。', '0', '1', '2015-01-19 09:47:11');
INSERT INTO `t_course` VALUES ('6021', '19,', ',,', '0', '快乐动物村', '0', '0', '2', null, null, '1', '10.00', null, null, '602101,', '3', '1/6021.png', null, '布奇、阿迪、小米、莫莫、小艾是布奇乐乐园的五个小伙伴。布奇是小狮子，他很勇敢；阿迪是小猴子，他爱思考；小米是小松鼠，她很细心；莫莫是小熊猫，他很大方；小艾是个小女孩，她热情、快乐，喜欢帮助别人。五个小伙伴一起生活，一起学习，他们乐于探索，认', '0', '1', '2015-01-19 09:47:26');
INSERT INTO `t_course` VALUES ('6022', '19,', '4,6,3', '0', '中国古代经典故事绘本', '0', '0', '2', null, null, '1', '10.00', null, null, '602201,', '3', '1/6022.png', null, '这是一部流传千古，感人至深的经典，是一部真正受益终身的动画片，它可以真正奠定孩子做人根基，也是一部真正改变您人际关系的动漫宝典。', '0', '1', '2015-01-19 18:22:15');
INSERT INTO `t_course` VALUES ('6027', '19,22,', ',,', '0', '英蕊英语', '0', '0', '2', null, null, '1', '10.00', null, null, '602701,', '3', '1/6027.png', null, '以3—7岁幼儿为对象，以动画故事形式，设计了一个以英语对白的童话世界，在小朋友置身于一个个“主题故事”的过程中，培养他们学习英语的兴趣和信心。', '0', '1', '2015-01-19 09:47:47');
INSERT INTO `t_course` VALUES ('6028', '22,', '2', '0', '小土豆英语', '0', '0', '2', null, null, '1', '10.00', null, null, '602801,', '3', '1/6028.png', null, '边看，边听、边游戏，英语学习真容易。轻松看电视，快乐学英语。最适合中国孩子的第一部学英语的卡通片。', '0', '1', '2015-01-19 09:48:21');
INSERT INTO `t_course` VALUES ('6033', '26,', '7,8,9,4,6,3,2', '0', '儿童折纸基础', '0', '0', '2', null, null, '1', '10.00', null, null, '603301,', '', '1/6033.png', null, '折纸在我国有着悠久的历史和大量的爱好者，尤其受到少年儿童的喜爱。玩是孩子的天性，如何在孩子玩的过程中寓教于乐呢？折纸在这方面提供了一个很好的途径，孩子在折纸的过程中促进了手脑的协调和创造力。', '0', '1', '2015-01-19 09:48:45');
INSERT INTO `t_course` VALUES ('6034', '26,', '7,8,9,4,6,3,2', '0', '儿童剪纸视频教学', '0', '0', '2', null, null, '1', '10.00', null, null, '603401,', '', '1/6034.png', null, '《儿童剪纸》意在通过动脑动手，培养孩子的感知能力，实现学习与游戏、想象与表现、实践与认识的结合，起到科学地开发儿童智力，创造性地深化儿童的知识结构，有效地开发儿童智力的作用。', '0', '1', '2015-01-19 09:48:57');
INSERT INTO `t_course` VALUES ('6035', '26,', '4', '0', '幼儿创意美术小班 ', '0', '0', '2', null, null, '1', '10.00', null, null, '603501,', '3', '1/6035.png', null, '幼儿美术是3—7岁学龄前儿童所从事的造型艺术活动，反映幼儿对周围现实生活的认识和体验。主要分为绘画、手工和欣赏三大类。', '0', '1', '2015-01-19 09:49:07');
INSERT INTO `t_course` VALUES ('6036', '26,', '6', '0', '幼儿创意美术中班 ', '0', '0', '2', null, null, '1', '10.00', null, null, '603601,', '3', '1/6036.png', null, '幼儿美术是3—7岁学龄前儿童所从事的造型艺术活动，反映幼儿对周围现实生活的认识和体验。主要分为绘画、手工和欣赏三大类。', '0', '1', '2015-01-19 09:49:18');
INSERT INTO `t_course` VALUES ('6037', '26,', '3', '0', '幼儿创意美术大班 ', '0', '0', '2', null, null, '1', '10.00', null, null, '603701,', '', '1/6037.png', null, '幼儿美术是3—7岁学龄前儿童所从事的造型艺术活动，反映幼儿对周围现实生活的认识和体验。主要分为绘画、手工和欣赏三大类。', '0', '1', '2015-01-19 09:49:28');
INSERT INTO `t_course` VALUES ('6038', '26,', '3', '0', '幼儿橡皮泥手工制作 ', '0', '0', '2', null, null, '1', '10.00', null, null, '603801,', '', '1/6038.png', null, '橡皮泥是孩子们最喜欢的玩具之一,,孩子们可以用它做出各种各样的造型,发挥出自己的想象力。一盒五颜六色的橡皮泥,用这些美丽的泥巴做些什么呢?怎样让它们进行神奇..', '0', '1', '2015-01-19 09:49:38');
INSERT INTO `t_course` VALUES ('7007', '20,', '11', '0', '小升初系列之修改病句专项突破', '2', '1', '2', null, null, '1', '10.00', null, null, '700701,', '3', '1/7007.png', null, '病句辨析是综合了语法、逻辑、修辞、标点等知识的一项小升初必考内容。复习时，要认真弄清用词错误、语法错误、歧义等方面的各种病因。还要掌握辨认病句的基本且有效的方法。\n', '0', '1', '2015-01-17 18:40:55');
INSERT INTO `t_course` VALUES ('7011', '20,', '12', '0', '小升初系列之阅读理解专项突破', '2', '1', '2', null, null, '1', '10.00', null, null, '701101,', '1', '1/7011.png，0/7011.png', null, '小升初语文考试中，除写作外最大得分点。本专题将通过精读阅读和浏览、阅读一篇文章的基本步骤、怎样把握文章的中心句和过渡句等重难点进行详细讲解，确保快速提高阅读分数。', '0', '1', '2015-01-19 15:48:59');
INSERT INTO `t_course` VALUES ('7013', '20,', '5,10,11,12,13', '0', '小升初冲进名校语文作文专项突破', '2', '1', '2', null, null, '1', '10.00', null, null, '701301,', '2', '1/7013.png，0/7013.png', null, '作为小升初语文考试中的最重要部分。本专题从写作方法到不同类型的作文分析，深入解读《大纲》对小学生作文要求，教你快速写出好作文！\n', '0', '1', '2015-01-19 15:49:09');
INSERT INTO `t_course` VALUES ('7020', '20,', '12', '0', '小升初知识点：因数，倍数，质数（数的整除', '2', '2', '2', null, null, '1', '10.00', null, null, '702001,', '3', '1/7020.png', null, '因数、倍数，最大公因数和最小公倍数最常出现在小升初数学考试的选择与填空题部分。对于跟多学生，这部分也是数学学习的难点。其实只要掌握方法，问题就能迎刃而解！\n', '0', '1', '2015-01-19 15:49:18');
INSERT INTO `t_course` VALUES ('7030', '22,', '2,5,10,11,12', '0', '最强单词记忆法小学篇', '0', '3', '2', null, null, '1', '10.00', null, null, '703001,', '2', '1/7030.png，0/7030.png', null, '打破了传统的以字母顺序罗列的方式，将单词按照主题分类别；针对不同的单词，为学生提供切实有效的学习方法；每个单词的释义都配有贴近生活的简单例句。背单词，so easy！', '0', '1', '2015-01-17 18:51:19');
INSERT INTO `t_course` VALUES ('7031', '22,', '2', '0', '小学英语重难点突破：时态与句型', '0', '3', '2', null, null, '1', '10.00', null, null, '703101,', '3', '1/7031.png', null, '小学阶段学生考察学习一般现在时、一般过去时和一般将来时。小学阶段对句型的考察主要包括祈使句、感叹句、疑问句和复合句。这也是小学阶段语法的重点。', '0', '1', '2015-01-17 19:00:17');
INSERT INTO `t_course` VALUES ('7034', '22,', '11,12', '0', '小学英语重难点突破：阅读与写作', '0', '3', '2', null, null, '1', '10.00', null, null, '703401,', '3', '1/7034.png', null, '近年来，注重对阅读理解能力，和学生写作的考核已成为小升初的一大趋势。阅读理解与写作能力影响并制约听、说、读、写能力的形成和发展，英语教学大纲也把培养阅读能力作为一个主要的教学目标。', '0', '1', '2015-01-17 19:00:29');
INSERT INTO `t_course` VALUES ('7035', '22,', '2', '0', '开开心心学英文之地理篇', '0', '3', '2', null, null, '1', '10.00', null, null, '703501,', '3', '1/7035.png', null, '兴趣是最好的导师！孩子都喜欢新鲜有趣的东西。本专题带你游览伦敦塔，华盛顿，爱丁堡，迪士尼乐园……让孩子在欢乐中掌握五年级英语知识点。', '0', '1', '2015-01-17 19:00:55');
INSERT INTO `t_course` VALUES ('8001', '26,', '7,8,9,4,6,3,2', '0', '奥尔夫教学', '0', '0', '2', null, null, '1', '10.00', null, null, '800101,', '2', '1/8001.png，0/8001.png', null, '“奥尔夫音乐教学法”为联邦德国著名音乐家卡尔·奥尔夫所创建。这是一个独创的音乐教育体系。它赋予音乐教育以全新的反传统的观念和方法，已经对许多国家和地区的音乐教育产生了深刻的影响。', '1', '1', '2015-01-19 16:53:32');
INSERT INTO `t_course` VALUES ('9003', '25,', '7', '0', '我是大美人', '0', '0', '2', null, null, '1', '10.00', null, null, '900301,', '', '1/9003.png', null, '每个女人的早晨都是相似的忙乱,出门总是那么困难，时间总是不够用。今天起，不用再压缩睡眠时间啦！现在我们就教给你只要10分钟就能美美出门的10种妆容的秘诀', '0', '1', '2015-01-19 15:50:11');
INSERT INTO `t_course` VALUES ('9004', '25,', '7', '0', '宝宝计划免疫全攻略', '0', '0', '2', null, null, '1', '10.00', null, null, '900401,', '3', '1/9004.png', null, '儿童计划免疫是根据危害儿童健康的一些传染病，利用安全有效的疫苗，按照规定的免疫程序进行预防接种，提高儿童免疫力，以达到预防相应传染病的目的。', '0', '1', '2015-01-19 15:51:35');
INSERT INTO `t_course` VALUES ('9005', '25,', '7', '0', '超实用漂亮妈咪养颜健康瑜伽', '0', '0', '2', null, null, '1', '10.00', null, null, '900501,', '', '1/9005.png', null, '通过本套瑜伽体位法30式，让妈咪重拾信心，具丰胸养颜提臀的显著效果，矫正不良姿势，改善全身的气血循环，促进身体新陈代谢，用最少的运动量，在最短的时间拥有曼妙身材。', '0', '1', '2015-01-19 15:51:46');
INSERT INTO `t_course` VALUES ('9007', '25,', '7,8,9', '0', '安全防护', '0', '0', '2', null, null, '1', '10.00', null, null, '900701,', '', '1/9007.png', null, '学龄前的宝宝是最富于冒险精神的，所有的危险都不在话下，再高的地方也得爬上去；走路一定要走崎曲的、坑坑洼洼的，从幼儿园回来，经常会带点小伤。如何培养宝宝的安全意识、如何加强宝宝的自我保护意识呢?宝宝受伤之后又该如何处理呢？', '0', '1', '2015-01-19 15:52:04');
INSERT INTO `t_course` VALUES ('9010', '25,', '7,8,9', '0', '宝宝发展教育全攻略', '0', '0', '2', null, null, '1', '10.00', null, null, '901001,', '', '1/9010.png', null, '每个阶段的宝宝无论在生理还是心理上都各不相同，因此父母在育儿面临着各种复杂问题。发展与教育专题带您全面认识0-6岁宝宝每个阶段的的身体动作发展，认知发展，社会性发展，语言与交流发展。', '0', '1', '2015-01-19 15:52:22');
INSERT INTO `t_course` VALUES ('9011', '25,', '7', '0', '新生儿实用育儿宝典', '0', '0', '2', null, null, '1', '10.00', null, null, '901101,', '', '1/9011.png', null, '恭喜你荣升妈妈爸爸！宝宝刚出生，身体比较弱小，需要你的特殊护理。如何给宝宝换尿布？如何喂养宝宝？新生儿专题，教你关于新生儿护理与喂养的必备知识，伴您和宝宝一起顺利渡过新生儿期。', '0', '1', '2015-01-19 15:52:33');
INSERT INTO `t_course` VALUES ('9012', '25,', '7', '0', '280天胎教音乐全方案', '0', '0', '2', null, null, '1', '10.00', null, null, '901201,', '', '1/9012.png', null, '280天胎教音乐全方案。胎教音乐对孕妈妈、胎儿及其整体发育乃至一生有着深远的影响。节奏柔和、旋律优美的音乐细心呵护胎儿宝宝幼嫩听力，有效安抚准妈妈波动情绪。', '0', '1', '2015-01-19 15:52:51');
