INSERT INTO `t_course` VALUES ('3071', '20,', '2', '0', '人教小学语文同步课堂一年级下', '2', '1', '1', null, '马洪英', '1', '10.00', null, null, '307101,307102,307103,307104,307105,307106,307107,', '3', '1/3071.png', null, '本册以专题组织单元，以整合的方式组织教材内容。共设有：多彩的春天，家庭生活，保护环境等8个专题。每个单元从导语到识字课，课文，语文园地都围绕本单元专题安排。 ', '0', '1', '2015-02-03 12:19:44');
INSERT INTO `t_course` VALUES ('3072', '20,', '5', '0', '人教小学语文同步课堂二年级下', '2', '1', '1', null, '马洪英', '1', '10.00', null, null, '307108,', '3', '1/3072.png', null, '认识400个字，会写300个字。要求认识的字能读准字音，结合词句了解意思，不作字形分析，不要求书写。要求会写的字能读准字音，认清字形，理解意思，正确书写，练习在口头和书面表达中运用。有主动识字的愿望，在生活中自主识字。养成学生良好的写字习惯', '0', '1', '2015-01-29 17:26:10');
INSERT INTO `t_course` VALUES ('3073', '20,', '10', '0', '人教小学语文同步课堂三年级下', '2', '1', '1', null, '孙芬', '1', '10.00', null, null, '307301,307302,307303,307304,307305,307306,307307,307308,', '3', '1/3073.png', null, '本册教材共有8个单元,32篇课文，每个单元包括导语、课文、语文百花园三部分。要求学生认识250个生字，学习观察大自然，观察社会，书面与口头表达结合表达自己的观察所得。', '0', '1', '2015-01-30 14:56:50');
INSERT INTO `t_course` VALUES ('3074', '20,', '11', '0', '人教小学语文同步课堂四年级下', '2', '1', '1', null, '丁纳', '1', '10.00', null, null, '307401,307402,307403,307404,307405,307406,307407,307408,', '3', '1/3074.png', null, '本册共有课文32篇，要求学生认字200个，会写200个；能初步把握文章的主要内容，思想感情，能复述叙事课文的大意；会写简短的书信便条，能修改习作中有明显错误的词句。', '0', '1', '2015-01-22 17:29:29');
INSERT INTO `t_course` VALUES ('3075', '20,', '12', '0', '人教小学语文同步课堂五年级下', '2', '1', '1', null, '丁纳', '1', '10.00', null, null, '307501,307502,307503,307504,307505,307506,307507,307508,', '3', '1/3075.png', null, '全册共有课文28篇，精读和略读各14篇。这七组教材，每组由“导语”“课例”“口语交际•习作”“回顾•拓展”四部分组成。本学期将完成小学阶段认识3000个常用字的识字任务。', '0', '1', '2015-01-22 17:29:47');
INSERT INTO `t_course` VALUES ('3076', '20,', '13', '0', '人教小学语文同步课堂六年级下', '2', '1', '1', null, '丁纳', '1', '10.00', null, null, '307601,307602,307603,307604,307605,', '3', '1/3076.png', null, '本册教材共有课文21篇。要求学生会写80个字，累计2500个字；阅读方面能借助工具书……', '0', '1', '2015-01-24 18:32:12');
INSERT INTO `t_course` VALUES ('3077', '20,', '2', '0', '人教小学数学一年级下－2012', '2', '2', '1', null, null, '1', '10.00', null, null, '307701,307702,307703,307704,307705,307706,307707,', '3', '1/3077.png', null, '本学期的教学目标是学生能够认识计数单位“一”和“十”；能够比较熟练地计算20以内的退位减法；能用自己的语言描述长方形、正方形边的特征；认识人民币单位元、角、分等。', '0', '1', '2015-01-22 17:30:19');
INSERT INTO `t_course` VALUES ('3079', '20,', '5', '0', '人教小学数学二年级下－2013', '2', '2', '1', null, null, '1', '10.00', null, null, '307901,307902,307903,307904,307905,307906,307907,307908,307909,', '3', '1/3079.png', null, '本学期数学教学目标：认识计数单位百和千；会口算百以内的两位数加、减两位数；知道除法的含义；认识角，认识质量单位克和千克；理解统计的意义，会用简单的方法收集和整理数据等。', '0', '1', '2015-01-22 17:30:37');
INSERT INTO `t_course` VALUES ('3081', '20,', '10', '0', '人教小学数学三年级下', '2', '2', '1', null, null, '1', '10.00', null, null, '308101,308102,308103,308104,308105,308106,308107,308108,308109,', '3', '1/3081.png', null, '本册主要掌握的知识点是两位数乘除法，小数；认识位置与方向；理解面积，面积单位；认识时间单位年、月、日及24时计时法；能够用所学的乘、除法知识解决生活中的简单问题。', '0', '1', '2015-01-22 17:30:53');
INSERT INTO `t_course` VALUES ('3082', '20,', '11', '0', '人教小学数学四年级下', '2', '2', '1', null, null, '1', '10.00', null, null, '308201,308202,308203,308204,308205,308206,308207,308208,', '3', '1/3082.png', null, '本册教学主要包括：小数的意义与性质（重难点），小数的加法和减法，四则运算，运算定律与简便计算，三角形，位置与方向，折线统计图，数学广角和数学综合运用活动等。', '0', '1', '2015-01-22 17:31:08');
INSERT INTO `t_course` VALUES ('3083', '20,', '12', '0', '人教小学数学五年级下', '2', '2', '1', null, null, '1', '10.00', null, null, '308301,308302,308303,308304,308305,308306,308307,', '3', '1/3083.png', null, '本册的重难点在数与代数部分因数与倍数；分数的意义和性质以及分数的加法、减法；空间与图形”领域教学图形的变换和长方体和正方体；统计与概率部分众数和复式折线统计图。', '0', '1', '2015-01-22 17:31:23');
INSERT INTO `t_course` VALUES ('3084', '20,', '13', '0', '人教小学数学六年级下', '2', '2', '1', null, null, '1', '10.00', null, null, '308401,308402,308403,308404,308405,', '3', '1/3084.png', null, '本册教学包括下面一些内容：负数、圆柱与圆锥、比例、统计、数学广角、整理和复习等。圆柱与圆锥、比例和整理和复习是本册教材的重点教学内容，也是视频教学的重点部分。', '0', '1', '2015-01-22 17:31:37');
INSERT INTO `t_course` VALUES ('3085', '20,', '10', '0', '人教PEP小学英语三年级下－2012', '2', '3', '1', null, '万玉英', '1', '10.00', null, null, '308501,308502,308503,308504,308505,308506,', '3', '1/3085.png', null, '本学期需要达成的目标：能听懂、会说12组会话，并能进行简单的交流；能听说、认读（包括动物、食品、数字、同学、家庭成员、数字等6个话题）的单词，并能简单的运用。', '0', '1', '2015-01-22 17:31:54');
INSERT INTO `t_course` VALUES ('3086', '20,', '11', '0', '人教PEP小学英语四年级下－2013', '2', '3', '1', null, '李艳', '1', '10.00', null, null, '308601,308602,308603,308604,308605,308606,', '3', '1/3086.png', null, '本册教材分为六个单元，要求学生能听懂、会说12组会话，并能进行简单的交流；能听、说 认读60个单词和词组并能听、说、读、写61个单词和24个句子（包括学校、时间等八个话题）。', '0', '1', '2015-01-26 11:25:57');
INSERT INTO `t_course` VALUES ('3088', '20,', '12', '0', '人教PEP小学英语五年级下', '2', '3', '1', null, '李艳', '1', '10.00', null, null, '308801,308802,308803,308804,308805,308806,', '3', '1/3088.png', null, '本学期教学目标是要求学生养成良好的听、读、说英语的习惯；初步养成良好的书写习惯；能按三会与四会的要求掌握所学词语；运用所学进行简单的日常交流并做到大胆开口，发音清楚正确。\n', '0', '1', '2015-01-22 17:32:46');
INSERT INTO `t_course` VALUES ('3089', '20,', '13', '0', '人教PEP小学英语六年级下', '2', '3', '1', null, '李艳，jean', '1', '10.00', null, null, '308901,308902,308903,308904,', '3', '1/3089.png', null, '本学期要求学生能听、说、读、写61个单词和短语以及7组句子和4个单句。能在真实语境中正确运用并能读懂简短语篇；完成2个自我评价活动；了解2项简单的中西方文化知识等。', '0', '1', '2015-01-26 11:26:15');
INSERT INTO `t_course` VALUES ('4005', '27,', '14,15,16', '0', '中考语文满分作文必备技巧', '2', '1', '2', null, null, '1', '10.00', null, null, '400501,400502,400503,400504,', '2,5', '1/4005.png，0/4005.png', null, '众所周知，语文成绩的提高与作文关系密切。如何提高学生的作文水平，是提高语文成绩的第一要务。这里我们汇总了中考常见作文类型、应考指导及一些实用的写作技巧，希望对初三生备考会有所帮助。', '0', '1', '2015-01-22 16:02:49');
INSERT INTO `t_course` VALUES ('4012', '27,', '16', '0', '中考语文阅读理解复习攻略', '1', '1', '2', null, null, '1', '10.00', null, null, '401201,', '1', '1/4012.png，0/4012.png', null, '“要点概括”是中考现代文阅读考查的第一个考点。在通读课文的基础上，理清思路，理解主要内容；具体考查考生在词句理解、文意把握、要点概括、内容探究、作品感受等方面的表现。', '0', '1', '2015-01-21 19:18:21');
INSERT INTO `t_course` VALUES ('4016', '27,', '14,16', '0', '中考数学方程专题复习突破', '2', '2', '2', null, null, '1', '10.00', null, null, '401601,401602,401603,401604,401605,', '1', '1/4016.png，0/4016.png', null, '方程的概念和原理了解， 一元一次、一元二次方程，二元一次方程组的解法；方程的有关应用题(特别是行程、工程问题)', '0', '1', '2015-01-21 19:15:50');
INSERT INTO `t_course` VALUES ('4025', '27,', '14,15,16', '0', '中考数学几何考点复习之相似形', '2', '2', '2', null, null, '1', '10.00', null, null, '402501,', '1', '1/4025.png，0/4025.png', null, '三角形的性质、判定定理及应用，特别是直角三角形斜边上的高的性质、直角三角形相似的判定，相似三角形中的分类讨论、以相似三角形为背景的实际运用能力；相关作图。', '0', '1', '2015-01-21 19:16:49');
INSERT INTO `t_course` VALUES ('4033', '27,22,', '16', '0', '中考英语必知语法要点：句子类型', '2', '0', '2', null, null, '1', '10.00', null, null, '403301,', '3,5', '1/4033.png', null, '句子的类型一共有三种：简单句、并列句和复合句。复合句中的宾语从句与状语从句是中考的重要考查内容。陈述句与疑问句亦是中考常考内容，其中反意疑问句的回答是易错点，需特别注意。', '0', '1', '2015-01-23 10:33:31');
INSERT INTO `t_course` VALUES ('4045', '27,', '14,15,16', '0', '中考物理备考专项复习', '2', '0', '2', null, null, '1', '10.00', null, null, '404501,404502,404503,404504,', '2', '1/4045.png，0/4045.png', null, '初中物理的热学是十分难学的单元章节。下面是关于初中热学的相关知识点。同学们可以根据这一汇总进行复习或者是预习，会有很好的学习效果。', '0', '1', '2015-01-22 10:51:05');
INSERT INTO `t_course` VALUES ('4058', '27,', '16', '0', '中考化学考点之水与溶液：溶液', '2', '0', '2', null, null, '1', '10.00', null, null, '405801,', '2', '1/4058.png，0/4058.png', null, '溶液、溶质、溶剂的认识，溶液中溶质的质量分数计算的具体情况', '0', '1', '2015-01-21 19:09:02');
INSERT INTO `t_course` VALUES ('4064', '27,', '16', '0', '中考化学重难点突破专项', '2', '0', '2', null, null, '1', '10.00', null, null, '406401,406402,406403,', '3,5', '1/4064.png', null, '中考中化学计算题是必不可少的题型,主要包括化学式计算、利用化学方式计算和有关溶液的计算三部分。其试题情境多与生产生活、社会热点、科学前沿相结合，试题题干多以文字信息和标签、表格、图像信息相结合的形式呈现，从解决实际问题出发，考查学生综合运用', '0', '1', '2015-01-22 16:05:48');
INSERT INTO `t_course` VALUES ('4068', '27,', '16', '0', '世界近代史', '1', '0', '2', null, null, '1', '10.00', null, null, '406801,', '3', '1/4068.png，0/4068.png', null, '资产阶级革命时代（十四、十五世纪——工业革命） ，自由资本主义时代（工业革命——二十世纪初）的主要历史事件的数学掌握。', '0', '1', '2015-01-20 19:27:20');
INSERT INTO `t_course` VALUES ('4069', '27,', '14', '0', '初中地理地球与地图', '1', '0', '2', null, null, '1', '10.00', null, null, '406901,406902,406903,', '3', '1/4069.png，0/4069.png', null, '了解地球的一般情况，如形状、大小、质量等。熟练掌握地球上的经纬网，并会运用经纬线知识解决实际地理问题。掌握地球的自转运动、公转运动特点及其地理意义。熟练运用地球运动知识分析常见地理现象和事实。', '0', '1', '2015-01-21 15:23:42');
INSERT INTO `t_course` VALUES ('4070', '27,', '15,16', '0', '中考政治总复习核心考点梳理', '1', '0', '2', null, null, '1', '10.00', null, null, '407001,407002,407003,407004,407005,407006,407007,', '3', '1/4070.png，0/4070.png', null, '公民的权利和义务', '0', '1', '2015-01-20 19:27:58');
INSERT INTO `t_course` VALUES ('4071', '27,26,', '14,15,16', '0', '奥尔夫音乐教学', '2', '0', '2', null, null, '1', '10.00', null, null, '407101,', '3', '1/4071.png，0/4071.png', null, '“奥尔夫音乐教学法”为联邦德国著名音乐家卡尔·奥尔夫所创建。这是一个独创的音乐教育体系。它赋予音乐教育以全新的反传统的观念和方法，已经对许多国家和地区的音乐教育产生了深刻的影响。认真研究奥尔夫的音乐教育思想和体系，我们会得到许多有益的启示。', '0', '1', '2015-01-26 11:26:30');
INSERT INTO `t_course` VALUES ('5001', '28,', '17,18', '0', '冲刺高考语文之修辞手法篇', '2', '1', '2', null, null, '1', '10.00', null, null, '500101,500102,', '3,5', '1/5001.png', null, '高中常考修辞手法及基本文学素养', '0', '1', '2015-01-22 16:08:35');
