/*Table structure for table `t_credit_rule` */

DROP TABLE IF EXISTS `t_credit_rule`;

CREATE TABLE `t_credit_rule` (
  `id` mediumint(8) NOT NULL AUTO_INCREMENT,
  `creditId` smallint(5) NOT NULL COMMENT '积分ID',
  `keyName` varchar(20) NOT NULL COMMENT '积分规则KEY',
  `name` varchar(60) NOT NULL COMMENT '积分规则名称',
  `timeType` tinyint(1) NOT NULL DEFAULT '0' COMMENT '规则时间类型，0为不限，1为全局，2为按月，3为按天',
  `timeNum` smallint(5) DEFAULT '1' COMMENT '时间长度',
  `ruleTypeNum` smallint(5) NOT NULL DEFAULT '0' COMMENT '根据上面规则类型的次数上限,0为不限',
  `creditNum` int(10) NOT NULL DEFAULT '0' COMMENT '积分数量',
  `ruleType` tinyint(1) DEFAULT '1' COMMENT '积分赚取还是消耗，默认1为赚取，2为消耗',
  `status` tinyint(1) NOT NULL DEFAULT '1' COMMENT '状态',
  `addTime` datetime NOT NULL COMMENT '添加时间',
  `modTime` datetime NOT NULL COMMENT '修改时间',
  PRIMARY KEY (`id`)
